sys_script_client.script:
function onSubmit() {
    var action1 = g_form.getActionName();
    var timeSpent = g_form.getValue('time_spent_minutes');
    if (action1 == 'proposeSolution') {
        if (timeSpent == '') {
           g_form.showFieldMsg("time_spent_minutes", 'Time Spent (Minutes) are required.', "error");
        g_form.setMandatory("time_spent_minutes", true);
        g_form.addErrorMessage("To propose a solution, you must select a Time Spent (Minutes).");

        return false;
        }



    }
}
