sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

         var url = "/esc?" + "&id=ticket&table=sn_customerservice_case&sys_id="+current.sys_id; 
var btnapprove = "Take me to the Case";
var buttonHtml = "<a href='" + gs.getProperty('glide.servlet.uri') + url + "' target='_blank' style='background-color: #4CAF50; color: white; padding: 10px 20px; text-align: center; text-decoration: none; display: inline-block; border-radius: 4px;'>" + btnapprove + "</a>";
template.print(buttonHtml);

})(current, template, email, email_action, event);
